namespace Faonni\ConfigAjaxButton\Controller\Adminhtml\System\Config\Validate;

use Magento\Framework\Controller\Result\JsonFactory;

/**
 * AjaxButton validation controller
 */
class Button extends \Faonni\ConfigAjaxButton\Controller\Adminhtml\System\Config\Validate
{
    /**
     * @var JsonFactory
     */
    protected $resultJsonFactory;

    /**
     * @param \Magento\Backend\App\Action\Context $context
     * @param JsonFactory $resultJsonFactory
     */
    public function __construct(
        \Magento\Backend\App\Action\Context $context,
        JsonFactory $resultJsonFactory
    ) {
        parent::__construct($context);
        $this->resultJsonFactory = $resultJsonFactory;
    }

    /**
     * Check whether vat is valid
     *
     * @return \Magento\Framework\Controller\Result\Json
     */
    public function execute()
    {
        $eventResponse = new \Magento\Framework\DataObject([
            'valid'   => 0,
            'message' => ''
        ]);
        $this->_eventManager->dispatch(
            'adminhtml_ajax_button_response',
            ['response' => $eventResponse, 'request' => $this->getRequest()]
        );        
        /** @var \Magento\Framework\Controller\Result\Json $resultJson */
        $resultJson = $this->resultJsonFactory->create();
        return $resultJson->setData([
            'valid' => $eventResponse->getValid(),
            'message' => $eventResponse->getMessage(),
        ]);
    }
}
